def translateES(word):
    translation=valyrian_to_spanish.get(word)
    if translation:
        return translation

    translation=spanish_to_valyrian.get(word)
    if translation:
        return JsonResponse({ resultado: translation })

    raise Exception("La palabra \"{0}\" no existe.".format(word))


def translateCA(word):
    translation=valyrian_to_catalan.get(word)
    if translation:
        return translation

    translation=catalan_to_valyrian.get(word)
    if translation:
        return translation

    raise Exception("La paraula \"{0}\" no existeix.".format(word))

def translateEN(word):
    translation=valyrian_to_english.get(word)
    if translation:
        return translation

    translation=english_to_valyrian.get(word)
    if translation:
        return translation

    raise Exception("Word \"{0}\" does not exist.".format(word))
